### SQL
SELECT r.area_name,(h.women_age_15_years_and_above_wih_elevated_blood_pressure_sys+h.men_age_15_years_and_above_wih_elevated_blood_pressure_systo) AS total FROM healthcare_stats h JOIN regions r ON h.state=r.state JOIN tru t ON h.tru_id=t.id WHERE t.name='Total' ORDER BY total DESC LIMIT 1;

