### SQL
SELECT h.institutional_births_in_the_5_years_before_the_survey_ FROM healthcare_stats h JOIN regions r ON h.state=r.state JOIN tru t ON h.tru_id=t.id WHERE r.area_name='Gujarat' AND t.name='Urban';

