### SQL
SELECT t.name,AVG(h.men_age_15_years_and_above_wih_elevated_blood_pressure_systo+h.women_age_15_years_and_above_wih_elevated_blood_pressure_sys) FROM healthcare_stats h JOIN tru t ON h.tru_id=t.id WHERE h.state=(SELECT state FROM regions WHERE area_name='Tamil Nadu') GROUP BY t.name;

