### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state=r.state JOIN tru t ON h.tru_id=t.id WHERE t.name='Total' ORDER BY CAST(h.infant_mortality_rate_per_1000_live_births AS DOUBLE PRECISION) DESC LIMIT 1;

