### SQL
SELECT r.area_name,ABS(MAX(CASE WHEN t.name='Urban' THEN CAST(h.women_age_1549_years_who_are_overweight_or_obese_bmi_250_kgm AS DOUBLE PRECISION) END)-MAX(CASE WHEN t.name='Rural' THEN CAST(h.women_age_1549_years_who_are_overweight_or_obese_bmi_250_kgm AS DOUBLE PRECISION) END)) AS gap FROM healthcare_stats h JOIN regions r ON h.state=r.state JOIN tru t ON h.tru_id=t.id GROUP BY r.area_name ORDER BY gap DESC LIMIT 1;

