### SQL
SELECT t.name,AVG(h.children_age_1223_months_who_received_most_of_their_vaccinat) FROM healthcare_stats h JOIN tru t ON h.tru_id=t.id GROUP BY t.name ORDER BY AVG(h.children_age_1223_months_who_received_most_of_their_vaccinat) ASC;

