### SQL
SELECT r.area_name,ABS(h.children_age_1223_months_fully_vaccinated_based_on_informati-h.children_age_1223_months_who_received_most_of_their_vaccinat) AS gap FROM healthcare_stats h JOIN regions r ON h.state=r.state JOIN tru t ON h.tru_id=t.id WHERE t.name='Total' ORDER BY gap DESC LIMIT 1;

