### SQL
SELECT r.area_name,(h.women_age_15_years_and_above_wih_high_or_very_high_140_mgdl_+h.men_age_15_years_and_above_wih_high_or_very_high_140_mgdl_bl+h.men_age_15_years_and_above_wih_elevated_blood_pressure_systo+h.women_age_15_years_and_above_wih_elevated_blood_pressure_sys) AS burden FROM healthcare_stats h JOIN regions r ON h.state=r.state JOIN tru t ON h.tru_id=t.id WHERE t.name='Total' ORDER BY burden DESC LIMIT 1;

