### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state=r.state JOIN tru t ON h.tru_id=t.id GROUP BY r.area_name HAVING MAX(CASE WHEN t.name='Rural' THEN CAST(h.children_age_1223_months_fully_vaccinated_based_on_informati AS DOUBLE PRECISION) END)>MAX(CASE WHEN t.name='Urban' THEN CAST(h.children_age_1223_months_fully_vaccinated_based_on_informati AS DOUBLE PRECISION) END);

