### SQL
SELECT r.area_name,(h.all_women_15_49_who_are_anaemic22_+h.women_age_1549_years_who_are_overweight_or_obese_bmi_250_kgm) AS female_burden FROM healthcare_stats h JOIN regions r ON h.state=r.state JOIN tru t ON h.tru_id=t.id WHERE t.name='Total' ORDER BY female_burden DESC LIMIT 1;

