### SQL
SELECT r.area_name,ABS(MAX(CASE WHEN t.name='Urban' THEN h.women_age_15_years_and_above_wih_high_or_very_high_140_mgdl_ END)-MAX(CASE WHEN t.name='Rural' THEN h.women_age_15_years_and_above_wih_high_or_very_high_140_mgdl_ END)) AS diff FROM healthcare_stats h JOIN regions r ON h.state=r.state JOIN tru t ON h.tru_id=t.id GROUP BY r.area_name ORDER BY diff ASC LIMIT 1;

