### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state=r.state JOIN tru t ON h.tru_id=t.id GROUP BY r.area_name HAVING SUM(CASE WHEN t.name='Rural' THEN h.women_age_15_years_and_above_wih_high_or_very_high_140_mgdl_ END)>SUM(CASE WHEN t.name='Urban' THEN h.women_age_15_years_and_above_wih_high_or_very_high_140_mgdl_ END);

