### SQL
SELECT r.area_name,ABS((h.women_age_15_years_and_above_wih_high_or_very_high_140_mgdl_+h.women_age_1549_years_who_are_overweight_or_obese_bmi_250_kgm)-(h.men_age_15_years_and_above_wih_high_or_very_high_140_mgdl_bl+h.men_age_1549_years_who_are_overweight_or_obese_bmi_250_kgm2_)) AS imbalance FROM healthcare_stats h JOIN regions r ON h.state=r.state JOIN tru t ON h.tru_id=t.id WHERE t.name='Total' ORDER BY imbalance DESC LIMIT 1;

