### SQL
SELECT r.area_name,ABS(MAX(CASE WHEN t.name='Urban' THEN h.all_women_15_49_who_are_anaemic22_ END)-MAX(CASE WHEN t.name='Rural' THEN h.all_women_15_49_who_are_anaemic22_ END)) AS gap FROM healthcare_stats h JOIN regions r ON h.state=r.state JOIN tru t ON h.tru_id=t.id GROUP BY r.area_name ORDER BY gap DESC LIMIT 1;

