### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state=r.state JOIN tru t ON h.tru_id=t.id WHERE h.women_age_15_years_and_above_wih_high_or_very_high_140_mgdl_>h.women_age_15_years_and_above_wih_elevated_blood_pressure_sys AND t.name='Total';

