### SQL
SELECT r.area_name,h.men_age_15_years_and_above_wih_high_or_very_high_140_mgdl_bl+h.women_age_15_years_and_above_wih_high_or_very_high_140_mgdl_ AS working_burden FROM healthcare_stats h JOIN regions r ON h.state=r.state JOIN tru t ON h.tru_id=t.id WHERE t.name='Total' ORDER BY working_burden DESC LIMIT 1;

