### SQL
SELECT r.area_name,(h.children_age_1223_months_fully_vaccinated_based_on_informati-h.children_under_5_years_who_are_underweight_weightforage18_) AS score FROM healthcare_stats h JOIN regions r ON h.state=r.state JOIN tru t ON h.tru_id=t.id WHERE t.name='Total' ORDER BY score DESC LIMIT 1;

