### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state=r.state JOIN tru t ON h.tru_id=t.id GROUP BY r.area_name HAVING AVG(CASE WHEN t.name='Rural' THEN h.children_under_5_years_who_are_underweight_weightforage18_ END)>AVG(CASE WHEN t.name='Urban' THEN h.children_under_5_years_who_are_underweight_weightforage18_ END);

