### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state=r.state JOIN tru t ON h.tru_id=t.id WHERE h.infant_mortality_rate_per_1000_live_births>h.institutional_births_in_the_5_years_before_the_survey_ AND t.name='Total';

