### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state=r.state JOIN tru t ON h.tru_id=t.id WHERE h.institutional_births_in_the_5_years_before_the_survey_>h.women_age_3049_years_ever_undergone_a_screening_test_for_cer AND t.name='Total';

