### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state=r.state JOIN tru t ON h.tru_id=t.id GROUP BY r.area_name HAVING AVG(CASE WHEN t.name='Urban' THEN h.children_age_1223_months_fully_vaccinated_based_on_informati END)=AVG(CASE WHEN t.name='Rural' THEN h.children_age_1223_months_fully_vaccinated_based_on_informati END);

