### SQL
SELECT r.area_name,(h.children_under_5_years_who_are_underweight_weightforage18_+h.children_age_1223_months_who_received_most_of_their_vaccinat) AS preventable FROM healthcare_stats h JOIN regions r ON h.state=r.state JOIN tru t ON h.tru_id=t.id WHERE t.name='Total' ORDER BY preventable DESC LIMIT 1;

