### SQL
SELECT r.area_name,(CAST(h.children_age_1223_months_fully_vaccinated_based_on_informati AS DOUBLE PRECISION)-CAST(h.infant_mortality_rate_per_1000_live_births AS DOUBLE PRECISION)) AS score FROM healthcare_stats h JOIN regions r ON h.state=r.state JOIN tru t ON h.tru_id=t.id WHERE t.name='Total' ORDER BY score DESC LIMIT 1;

