### SQL
SELECT r.area_name,ABS(CAST(h.children_age_1223_months_fully_vaccinated_based_on_informati AS DOUBLE PRECISION)-(100-CAST(h.infant_mortality_rate_per_1000_live_births AS DOUBLE PRECISION))) AS link FROM healthcare_stats h JOIN regions r ON h.state=r.state JOIN tru t ON h.tru_id=t.id WHERE t.name='Total' ORDER BY link ASC LIMIT 1;

