### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state=r.state JOIN tru t ON h.tru_id=t.id WHERE (h.women_age_15_years_and_above_wih_high_or_very_high_140_mgdl_+h.men_age_15_years_and_above_wih_high_or_very_high_140_mgdl_bl)>(h.children_age_1223_months_who_received_most_of_their_vaccinat+h.children_under_5_years_who_are_underweight_weightforage18_) AND t.name='Total';

