### SQL
SELECT r.area_name FROM healthcare_stats h JOIN regions r ON h.state=r.state JOIN tru t ON h.tru_id=t.id WHERE h.children_age_1223_months_fully_vaccinated_based_on_informati>90 AND h.women_age_15_years_and_above_wih_high_or_very_high_140_mgdl_>300000 AND t.name='Total';
